INSERT INTO `wp_snippets` (`id`, `name`, `description`, `code`, `tags`, `scope`, `priority`, `active`, `modified`, `revision`, `cloud_id`) VALUES
(1, 'Make upload filenames lowercase', 'Makes sure that image and file uploads have lowercase filenames.\n\nThis is a sample snippet. Feel free to use it, edit it, or remove it.', 'add_filter( \'sanitize_file_name\', \'mb_strtolower\' );', 'sample, media', 'global', 10, 0, '2023-12-29 14:01:33', 2, NULL),
(2, 'Disable admin bar', 'Turns off the WordPress admin bar for everyone except administrators.\n\nThis is a sample snippet. Feel free to use it, edit it, or remove it.', 'add_action( \'wp\', function () {\n	if ( ! current_user_can( \'manage_options\' ) ) {\n		show_admin_bar( false );\n	}\n} );', 'sample, admin-bar', 'front-end', 10, 0, '2023-12-29 14:01:33', 2, NULL),
(3, 'Allow smilies', 'Allows smiley conversion in obscure places.\n\nThis is a sample snippet. Feel free to use it, edit it, or remove it.', 'add_filter( \'widget_text\', \'convert_smilies\' );\nadd_filter( \'the_title\', \'convert_smilies\' );\nadd_filter( \'wp_title\', \'convert_smilies\' );\nadd_filter( \'get_bloginfo\', \'convert_smilies\' );', 'sample', 'global', 10, 0, '2023-12-29 14:01:33', 2, NULL),
(4, 'Current year', 'Shortcode for inserting the current year into a post or page..\n\nThis is a sample snippet. Feel free to use it, edit it, or remove it.', '<?php echo date( \'Y\' ); ?>', 'sample, dates', 'content', 10, 0, '2023-12-29 14:01:33', 2, NULL),
(5, '', '', '// Code Start\n// To change add to cart text on single product page\nadd_filter( \'woocommerce_product_single_add_to_cart_text\', \'woocommerce_custom_single_add_to_cart_text\' ); \nfunction woocommerce_custom_single_add_to_cart_text() {\n    return __( \'Rezerviši\', \'woocommerce\' );  // Replace \"Rezerviši\" text with your own text\n}\n\n// To change add to cart text on product archives(Collection) page\nadd_filter( \'woocommerce_product_add_to_cart_text\', \'woocommerce_custom_product_add_to_cart_text\' );  \nfunction woocommerce_custom_product_add_to_cart_text() {\n    return __( \'Rezerviši\', \'woocommerce\' );  // Replace \"Rezerviši\" text with your own text\n}\n// Code End', '', 'global', 10, 1, '2023-12-29 14:03:28', 3, NULL);
